mapper.select:
SELECT profit_list.profit, /* 收益金额 */ profit_list.order_id, profit_list.order_sn, profit_list.goods_id, profit_list.goods_name, profit_list.user_id, u.`username`, profit_list.`order_goods_actual_price`, /* 成交价格 */ DATE_FORMAT(profit_list.`add_time`, "%Y-%m-%d %H:%i:%S") AS add_time, profit_type /* x为下级，xx为下下级*/ FROM ( SELECT info.order_goods_actual_price * go.`first_profit` AS profit, info.order_id, info.order_sn, info.goods_id, info.goods_name, info.user_id, info.`order_goods_actual_price`, info.add_time, 'x' as profit_type FROM litemall_order_goods_info info LEFT JOIN litemall_goods go ON info.`goods_id` = go.id WHERE 1 = 1 AND info.is_extract = 0 AND info.order_goods_status IN ('201','301','302','401','402','403','501') AND info.user_id IN (SELECT id FROM litemall_user WHERE p_id = #{user_id,jdbcType=INTEGER}) UNION ALL SELECT info.order_goods_actual_price * go.`second_profit` AS profit, info.order_id, info.order_sn, info.goods_id, info.goods_name, info.user_id, info.`order_goods_actual_price`, info.add_time, 'xx' as profit_type FROM litemall_order_goods_info info LEFT JOIN litemall_goods go ON info.`goods_id` = go.id WHERE 1 = 1 AND info.is_extract = 0 AND info.order_goods_status IN ('201','301','302','401','402','403','501') AND info.user_id IN (SELECT id FROM litemall_user WHERE p_id IN (SELECT id FROM litemall_user WHERE p_id = #{user_id,jdbcType=INTEGER})) ) profit_list LEFT JOIN litemall_user u ON u.id = profit_list.user_id WHERE 1 = 1
AND order_id = #{orderId,jdbcType=INTEGER}
AND order_sn = #{orderSn,jdbcType=VARCHAR}
ORDER BY profit_list.`add_time` DESC

SELECT ifnull(sum(profit_money),0) AS sum_profit_money
FROM litemall_earnings
WHERE operation = 2 AND profit_user_id = #{user_id,jdbcType=INTEGER};
