select t1.id, t1.user_id, t1.order_sn, t1.order_status, t1.consignee, t1.mobile, t1.address, t1. goods_price, t1.freight_price, t1.coupon_id,t1.coupon_price, t1.integral_price, t1.order_price, t1.actual_price, t1.pay_id, t1.pay_status,t1.pay_type, t1.ship_sn, t1.ship_channel, t1.ship_start_time, t1.ship_end_time, t1.confirm_time, t1.end_time, t1.add_time,t1.get_store_id, date_format(t1.pay_time,'%Y-%m-%d %H:%i:%s') AS "payTimeDisp", date_format(t1.ship_start_time,'%Y-%m-%d %H:%i:%s') AS "shipStartTimeDisp", date_format(t1.ship_end_time,'%Y-%m-%d %H:%i:%s') AS "shipEndTimeDisp", date_format(t1.confirm_time,'%Y-%m-%d %H:%i:%s') AS "confirmTimeDisp", date_format(t1.end_time,'%Y-%m-%d %H:%i:%s') AS "endTimeDisp", date_format(t1.add_time,'%Y-%m-%d %H:%i:%s') AS "addTimeDisp", t1.deleted,t2.username as "username", t2.member_id as "memberId" from litemall_order t1 left join litemall_user t2 on t1.user_id = t2.id where 1= 1
and t1.user_id = #{userId,jdbcType=INTEGER}
and t1.coupon_id = #{couponId,jdbcType=INTEGER}
and t1.order_sn = ${orderSn}
and t2.username like CONCAT('%',#{username},'%')
order by t1.add_time desc


select

  distinct


  ${column.value}

from litemall_order




  order by ${example.orderByClause}


