SELECT * FROM ( SELECT date_format(aus.create_time,'%Y-%m-%d %H:%i:%s') AS "createDate", aus.face_type faceType, ws.shop_name shopName, wcu.open_id wxOpenId, wcu.mobile mobile, wcu.weixin_nick_name wxNickName, wcu.gender gender, wcu.birthday birthday, wcu.icon icon, aus.user_id userId, aus.analyse_id analyseId, CONCAT(CAST(ROUND(wias.score0 * 100,2) AS CHAR(8) ),'%') AS "score0", CONCAT(CAST(ROUND(wias.score1 * 100,2) AS CHAR(8) ),'%') AS "score1", CONCAT(CAST(ROUND(wias.score2 * 100,2) AS CHAR(8) ),'%') AS "score2", CONCAT(CAST(ROUND(wias.score3 * 100,2) AS CHAR(8) ),'%') AS "score3", ROUND(wias.score4,0) score4, CONCAT(CAST(ROUND(wias.score5 * 100,2) AS CHAR(8) ),'%') AS "score5", CONCAT(CAST(ROUND(wias.score6 * 100,2) AS CHAR(8) ),'%') AS "score6", CONCAT(CAST(ROUND(wias.score7 * 100,2) AS CHAR(8) ),'%') AS "score7", CONCAT(CAST(ROUND(wias.score8 * 100,2) AS CHAR(8) ),'%') AS "score8", CONCAT(CAST(ROUND(wias.score9 * 100,2) AS CHAR(8) ),'%') AS "score9", CONCAT(CAST(ROUND(wias.score10 * 100,2) AS CHAR(8) ),'%') AS "score10", CONCAT(CAST(ROUND(wias.score11 * 100,2) AS CHAR(8) ),'%') AS "score11", CONCAT(CAST(ROUND(wias.score12 * 100,2) AS CHAR(8) ),'%') AS "score12", imas.source_img0 sourceImg0, imas.source_img1 sourceImg1, imas.source_img2 sourceImg2, imas.source_img3 sourceImg3, imas.source_img4 sourceImg4, imas.source_img5 sourceImg5, imas.source_img6 sourceImg6, imas.source_img7 sourceImg7, imas.source_img8 sourceImg8, imas.source_img9 sourceImg9, imas.source_img10 sourceImg10, imas.source_img11 sourceImg11, iao.original_zrg_img originalZrg, iao.original_bp_img originalBp, iao.original_zp_img originalZp, iao.original_yg_img originalYg, zo.zero_zrg_img zeroZrg, zo.zero_bp_img zeroBp, zo.zero_zp_img zeroZp, zo.zero_yg_img zeroYg, zo.zero_brown_img zeroBrown, zo.zero_red_img zeroRed, zo.zero_uv_img zeroUv, mk.image_red imageRed, mk.image_green imageGreen, mk.image_blue imageBlue, mk.image_black imageBlack, mk.image_white imageWhite, mk.image_gray imageGray, ias.small_zrg_img AS thumbZrg, ias.small_bp_img AS thumbBp, ias.small_zp_img AS thumbZp, ias.small_yg_img AS thumbYg, ss.source_small_img3 AS thumbUv, ss.source_small_img11 AS thumbRed, ss.source_small_img5 AS thumbBrown FROM ( SELECT * FROM( SELECT wia.analyse_id, wia.user_id, wia.shop_id, wia.create_time, wia.face_type FROM ( SELECT DISTINCT ia.user_id FROM wmw_shop sh INNER JOIN wmw_img_medical_analyse ia ON sh.id = ia.shop_id
) dia INNER JOIN wmw_img_medical_analyse wia ON dia.user_id = wia.user_id INNER JOIN wmw_shop ws ON ws.id = wia.shop_id AND wia. STATUS = 0 AND wia.type = 3
ORDER BY wia.create_time DESC ) ausc GROUP BY ausc.user_id ) aus LEFT JOIN wmw_img_medical_analyse_score wias ON aus.analyse_id = wias.analyse_id LEFT JOIN wmw_client_user wcu ON aus.user_id = wcu.id LEFT JOIN wmw_shop ws ON aus.shop_id = ws.id LEFT JOIN wmw_img_medical_analyse_zero zo ON zo.analyse_id = aus.analyse_id LEFT JOIN wmw_img_medical_analyse_mark mk ON mk.analyse_id = aus.analyse_id LEFT JOIN wmw_img_medical_analyse_small ias ON ias.analyse_id = aus.analyse_id LEFT JOIN wmw_img_medical_analyse_source imas ON imas.analyse_id = aus.analyse_id LEFT JOIN wmw_img_medical_analyse_original iao ON iao.analyse_id = aus.analyse_id LEFT JOIN wmw_img_medical_analyse_source_small ss ON ss.analyse_id = aus.analyse_id
where 1 = 1
and wcu.mobile = #{mobile,jdbcType=INTEGER}
) aaa ORDER BY createDate DESC

